import {
    call,
    takeLatest,
} from "redux-saga/effects";
import { SagaIterator } from "redux-saga";
import { bindAsyncAction } from "typescript-fsa-redux-saga";
import { getUser, triggerGetUser } from "./userTypes";


const getUserSaga = bindAsyncAction(getUser)(function* (): SagaIterator {
    try {
        const results = yield call(apiFetch);
        async function apiFetch() {
            let response = await fetch('https://test-unified.client-api.vyyer.id/api/v2/scans');

            if (response.ok) {
                let json = await response.json();
            } else {
                alert("Ошибка HTTP: " + response.status);
            }
        }
        return { balance: results }
    } catch (e) {
        console.log('error')
    }
});

export default function* functionExportSaga() {
    yield takeLatest(triggerGetUser.type, getUserSaga);
}
