import { useAuth0 } from '@auth0/auth0-react';
import React, { useEffect } from 'react'
import { Link } from 'react-router-dom';
import { OpenAPI } from "../../../api-client-codegen/core/OpenAPI";
import { triggerGetUser } from '../../../store/user';
import { useDispatch } from 'react-redux';
import { useAppSelector } from '../../../utils/hooks/redux';
import { Badge, Button, Image } from '@mantine/core';
import { ShowLoader } from '../../../utils/loader';
import { SizeType, VariantTypeLoader } from '../../../enum/mantine';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSignInAlt } from "@fortawesome/free-solid-svg-icons";
import { Header as HeaderStyle, ContainerHeader, ContainerHeaderController } from './header.styled'
import {UserButton} from './headerMenu'
import { IconProp } from '@fortawesome/fontawesome-svg-core';
OpenAPI.BASE = process.env.REACT_APP_SERVER_URL || ""

const Header = () => {
    const dispatch = useDispatch()
    const { loginWithRedirect, logout, isAuthenticated, user
        , getAccessTokenSilently
    } = useAuth0();
    const { balance, loading } = useAppSelector(state => state.user)
    const login = () => loginWithRedirect()
    const logoutRedirect = () => logout({ returnTo: window.location.origin, })

    useEffect(() => {
        if(isAuthenticated){
            dispatch(triggerGetUser())
        }
    }, [dispatch, isAuthenticated])

    const getUserCoin = async () => {
        const token = await getAccessTokenSilently();
        await navigator.clipboard.writeText(`${token}`);
    };

    return (
        <HeaderStyle>
            <ContainerHeader>
                <div>
                    <Link to={"/"}>
                        <Image
                            width={100}
                            src="https://cdn.rexy.ai/assets/rexy-logo.png"
                            alt="Logo"
                        />
                    </Link>
                </div>
                <div>
                    {!isAuthenticated ? (
                        <Button onClick={login} leftIcon={<FontAwesomeIcon icon={faSignInAlt as IconProp} />} variant="light" color="teal">
                            Sign In
                        </Button>
                    ) : (
                        <ContainerHeaderController>
                            {loading ? <ShowLoader variant={VariantTypeLoader.bars}/> : <Badge radius={SizeType.xs} color="teal">$ {balance.balance.toFixed(2)}</Badge>}

                            {/* <Avatar src={user?.picture}  radius={SizeType.xs} />

                            <p onClick={getUserCoin}>{user?.nickname}</p> */}
                            <div>
                                <UserButton getUserCoin={getUserCoin} logoutRedirect={logoutRedirect} image={user!.picture!} name={user!.nickname!} email={user!.email!} />
                            </div>

                            {/* <ActionIcon color="red" onClick={logoutRedirect} variant="light">
                                <FontAwesomeIcon icon={faSignOutAlt} />
                            </ActionIcon> */}
                        </ContainerHeaderController>
                    )}
                </div>
            </ContainerHeader>
        </HeaderStyle>
    )
}

export default Header
