import React, { FC, ReactNode } from 'react'
import PropTypes from 'prop-types'
import axios from 'axios'

import { useAuth0 } from "@auth0/auth0-react";

export const AxiosContext = React.createContext(axios)
interface Props {
  children: ReactNode;
}
const AxiosProvider: FC<Props> = ({ children }) => {
  const { getAccessTokenSilently } = useAuth0();
  console.log("CALL!");
  axios.interceptors.request.use(async (config) => {
    const accessToken = await getAccessTokenSilently();
    // const accessToken = 'ABCD'
    console.log(`TOKEN: ${accessToken}`);
    config!.headers!.Authorization = `Bearer ${accessToken}`
    return config;
  })

  return (
    // @ts-ignorec
    <AxiosContext.Provider>
      {children}
    </AxiosContext.Provider>
  )
}

AxiosProvider.defaultProps = {
}

AxiosProvider.propTypes = {
  // getAccessTokenSilently: PropTypes.func.isRequired,
  // instance: PropTypes.func.isRequired,
  children: PropTypes.any.isRequired,
}

export default AxiosProvider
